        <script>
        jQuery(document).ready(function($) {
            function detectmob() {
                if (navigator.userAgent.match(/Android/i) || navigator.userAgent.match(/webOS/i) || navigator.userAgent.match(/iPhone/i) || navigator.userAgent.match(/iPad/i) || navigator.userAgent.match(/iPod/i) || navigator.userAgent.match(/BlackBerry/i) || navigator.userAgent.match(/Windows Phone/i)) {
                    return true;
                } else {
                    return false;
                }
            }
            var t = {
                delay: 125,
                overlay: $(".fb-overlay"),
                widget: $(".fb-widget"),
                button: $(".fb-button")
            };
            setTimeout(function() {
                $("div.fb-livechat").fadeIn()
            }, 8 * t.delay);
            if (!detectmob()) {
                $(".ctrlq").on("click", function(e) {
                    e.preventDefault(), t.overlay.is(":visible") ? (t.overlay.fadeOut(t.delay), t.widget.stop().animate({
                        bottom: 0,
                        opacity: 0
                    }, 2 * t.delay, function() {
                        $(this).hide("slow"), t.button.show()
                    })) : t.button.fadeOut("medium", function() {
                        t.widget.stop().show().animate({
                            bottom: "20px",
                            opacity: 1
                        }, 2 * t.delay), t.overlay.fadeIn(t.delay)
                    })
                })
            }

            //hotline

            var hl = {
                delay: 125,
                overlay: $(".hl-overlay"),
                widget: $(".hl-widget"),
                button: $(".iconHotline")
            };
            setTimeout(function() {
                $("div.hl-livechat").fadeIn()
            }, 8 * hl.delay);
            if (!detectmob()) {
                $(".hlctrlq").on("click", function(e) {
                    e.preventDefault(), hl.overlay.is(":visible") ? (hl.overlay.fadeOut(hl.delay), hl.widget.stop().animate({
                        bottom: 0,
                        opacity: 0
                    }, 2 * hl.delay, function() {
                        $(this).hide("slow"), hl.button.show()
                    })) : hl.button.fadeOut("medium", function() {
                        hl.widget.stop().show().animate({
                            bottom: "20px",
                            opacity: 1
                        }, 2 * hl.delay), hl.overlay.fadeIn(hl.delay)
                    })
                })
            }
        });
        </script>
        
